// Pegar a mensagem do usuário
const userMessage = $input.item.json.body.message || $input.item.json.message || "Olá";

// Criar uma resposta simples
const responses = [
  `Entendo que você disse: "${userMessage}". Como posso te ajudar mais?`,
  `Obrigada por compartilhar isso comigo. Conte-me mais sobre como você está se sentindo.`,
  `Estou aqui para te ouvir. O que mais está em sua mente sobre "${userMessage}"?`,
  `Interessante. Como isso está fazendo você se sentir agora?`,
  `Agradeço por confiar em mim. Vamos explorar isso juntos. O que mais você gostaria de falar sobre "${userMessage}"?`
];

// Escolher uma resposta aleatória
const randomResponse = responses[Math.floor(Math.random() * responses.length)];

// Retornar o resultado
return {
  response: randomResponse,
  userMessage: userMessage,
  timestamp: new Date().toISOString()
};
